module.exports = async function(data) {
	var results = [];
    for (var i = 0; i < data.$trigger.keys.length; i++) {
        results.push({
            "item": data.$trigger.keys[i],
            "collection": `${data.$trigger.collection}`
        });
    }
	return results;
}

