module.exports = async function (data) {
    const result = {};

    Object.keys(data.$trigger.new).forEach((key) => {
        if (!Object.is(data.$trigger.new[key], data.$trigger.old[key])) {
            result[key] = data.$trigger.new[key];
        }
    });

    return result;
};
