var FormData = require('form-data');
const axios = require('axios');

module.exports = async function(data) {
    try {
        const fileUrl = `${data.$env.PUBLIC_URL}/assets/${data.get_export_file_meta.data.data[0].filename_disk}`;

    	var fileResponse = await axios.get(fileUrl, { responseType: 'stream' });

        const form = new FormData();
        form.append('file', fileResponse.data, 'import.json');

        const response = await axios.post(`${data.$env.UPSTREAM_URL}/utils/import/directus_files`, form, {
            headers: {
                ...form.getHeaders(),
                Authorization: `Bearer ${data.$env.CMS_TOKEN}`
            },
        });

        return {};
    }
    catch (e) {
        return { "error": e.message }
    }
}

